mapper.select:
select username, userid, email
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, email, joindate
from ssafy_member
where userid = #{userid}

select userid, username, userpwd, email, joindate
from ssafy_member
where userid = #{id} and username = #{name}

mapper.update:
update ssafy_member
set userpwd = #{userpwd}, email = #{email}
where userid = #{userid}

update ssafy_member
set username = #{username}, userpwd = #{userpwd}, email = #{email}
where userid = #{userid}
