mapper.select:
select noticeno, userid, subject, content, hit, regtime
from notice


		where subject like concat('%', #{word}, '%')


		where ${key} = #{word}


order by noticeno desc
limit #{start}, #{spp}

select count(noticeno) from notice
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select noticeno, userid, subject, content, hit, regtime
from notice
where noticeno = #{noticeno}

select max(noticeno)
from notice
where noticeno < #{noticeno}

select min(noticeno)
from notice
where noticeno > #{noticeno}

mapper.update:
update notice
set hit = hit + 1
where noticeno = #{noticeno}

update notice
set subject = #{subject}, content = #{content}
where noticeno = #{noticeno}

mapper.select:
select gallery_id, noticeno, userid, title, file_path, imgFullPath
from gallery
where noticeno = #{noticeno}
